CREATE DATABASE stauapp;
CREATE TABLE cameras (id char(6), abid char(10), description varchar(100), latitude double(12,2), longitude double(12,2), refcount int DEFAULT 0, PRIMARY KEY (id,abid));
CREATE TABLE images (inserttimestamp DateTime, camera_id char(6), image mediumblob, FOREIGN KEY fk_camera(camera_id) REFERENCES cameras(ID) ON UPDATE NO ACTION ON DELETE NO ACTION, PRIMARY KEY (inserttimestamp, camera_id));
CREATE TABLE masks (camera_id char(6), orientation bit, image mediumblob, FOREIGN KEY fk_camera(camera_id) REFERENCES cameras(ID) ON UPDATE NO ACTION ON DELETE NO ACTION, PRIMARY KEY(camera_id, orientation));